import { useRouter } from 'next/router';
import { useEffect, useState } from 'react';
import { gql, useQuery, ApolloProvider } from '@apollo/client';
import client from '../../utils/apolloClient';  // Adjust the import based on where you save apolloClient.js

const GET_RESTAURANT_BY_ID = gql`
  query getRestaurantById($id: ID!) {
    restaurant(id: $id) {
      id
      name
      description
      image {
        url
      }
      dishes {
        id
        name
        description
      }
    }
  }
`;

const API_URL = process.env.API_URL || 'http://localhost:1337';

const RestaurantPage = () => {
  const router = useRouter();
  const { id } = router.query;

  const { loading, error, data } = useQuery(GET_RESTAURANT_BY_ID, {
    variables: { id },
    skip: !id
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const restaurant = data.restaurant;

  return (
    <ApolloProvider client={client}>
      <div>
        <h1>{restaurant.name}</h1>
        <img src={`${API_URL}${restaurant.image.url}`} alt={restaurant.name} />
        <p>{restaurant.description}</p>

        <ul>
          {restaurant.dishes.map(dish => (
            <li key={dish.id}>
              <h2>{dish.name}</h2>
              <p>{dish.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </ApolloProvider>
  );
};

export default RestaurantPage;
