/* /components/Layout.js */

import React, { useState, useContext, useEffect } from "react";   // Modified this line to import useState and useEffect
import Head from "next/head";
import Link from "next/link";
import { Container, Nav, NavItem, Alert } from "reactstrap";    // Modified this line to import Alert
import AppContext from "./context";

const Layout = (props) => {
const [showSuccessMessage, setShowSuccessMessage] = useState(false); //Added this line for the success message state
const title = "Welcome to ZipLocalDish";

// Modified to include messageType and setMessageType
const { user, messageType, setMessageType } = useContext(AppContext); 

// Modified this useEffect
useEffect(() => {
  if (user) {
    if (messageType === 'newUser') {
      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 5000); // Hide after 5 seconds
    } else if (messageType === 'existingUser') {
      // Do something else, like showing a different message
    }
    setMessageType(''); // Reset messageType after it has been used
  }
}, [user, messageType]);  // Added messageType here

  return (
    <div>
      <Head>
        <title>{title}</title>
        <meta charSet="utf-8" />
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />
        <script src="https://js.stripe.com/v3" />
      </Head>
      <header>
      {/* Modified this line to show the success message based on messageType */}
      {showSuccessMessage && messageType === 'newUser' && <Alert color="success">Account successfully created!</Alert>}
        {showSuccessMessage && messageType === 'existingUser' && <Alert color="info">Welcome back!</Alert>}

        <style jsx>
          {`
            a {
              color: white;
            }
            h5 {
              color: white;
              padding-top: 11px;
            }
          `}
        </style>
        <Nav className="navbar navbar-dark bg-dark">
          <NavItem>
            <Link href="/">
              <a className="navbar-brand">Home</a>
            </Link>
          </NavItem>
          <NavItem>  {/* Added this part for the app name */}
            <span style={{ color: 'white', marginLeft: '30px' }}>From Local Chefs, &nbsp;&nbsp;&nbsp;To Your Doorstep—Fast! &nbsp;&nbsp;&nbsp;&nbsp;Welcome to ZipLocalDish!</span>
          </NavItem>
          <NavItem className="ml-auto">
            {user ? (
              <h5>{user.username}</h5>
            ) : (
              <Link href="/register">
                <a className="nav-link"> Sign up</a>
              </Link>
            )}
          </NavItem>
          <NavItem>
            {user ? (
              <Link href="/">
                <a
                  className="nav-link"
                  onClick={() => {
                    logout();
                    setUser(null);
                  }}
                >
                  Logout
                </a>
              </Link>
            ) : (
              <Link href="/login">
                <a className="nav-link">Sign in</a>
              </Link>
            )}
          </NavItem>
        </Nav>
      </header>
      <Container>{props.children}</Container>
    </div>
  );
};

export default Layout;
