import React, { useContext } from "react";
import { useRouter } from "next/router";
import { Button, Card, CardBody, CardTitle, Badge } from "reactstrap";
import AppContext from "./context"
import Link from "next/link"

function Cart() {
  // ... (your existing code for hooks and context)

  return (
    <div className="cart-container">
      <h1>Cart</h1>
      <Card className="cart-card">
        <CardTitle className="cart-title">Your Order:</CardTitle>
        <hr />
        <CardBody className="cart-body">
          <div className="item-header">
            <small>Items:</small>
          </div>
          <div className="item-list">
            {renderItems()}
          </div>
          <div className="checkout-section">
            {checkoutItems()}
          </div>
        </CardBody>
      </Card>
      <style jsx>{`
        .cart-container {
          font-family: Arial, sans-serif;
        }
        .cart-card {
          padding: 20px;
        }
        .cart-title {
          margin: 15px;
          font-size: 1.5em;
        }
        .cart-body {
          padding: 20px;
        }
        .item-header {
          margin-bottom: 12px;
          font-weight: bold;
        }
        .item-list {
          margin-bottom: 20px;
        }
        .checkout-section {
          margin-top: 15px;
        }
      `}</style>
    </div>
  );
}

export default Cart;
