import React, { useState, useContext } from "react";
import { FormGroup, Label, Input } from "reactstrap";
import fetch from "isomorphic-fetch";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import CardSection from "./cardSection";
import AppContext from "./context";
import Cookies from "js-cookie";

function CheckoutForm({ onOrderSuccess }) {
  const [data, setData] = useState({
    address: "",
    city: "",
    state: "",
    stripe_id: "",
  });
  const [error, setError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});
  const stripe = useStripe();
  const elements = useElements();
  const appContext = useContext(AppContext);

  function onChange(e) {
    const updateItem = (data[e.target.name] = e.target.value);
    setData({ ...data, updateItem });
  }

  function validateFields() {
    const errors = {};
    if (!data.address) errors.address = "Address is required";
    if (!data.city) errors.city = "City is required";
    if (!data.state) errors.state = "State is required";
    setFieldErrors(errors);
    return Object.keys(errors).length === 0;
  }

  async function submitOrder() {
    if (!validateFields()) {
      return;
    }
    const cardElement = elements.getElement(CardElement);
    const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
    const token = await stripe.createToken(cardElement);
    const userToken = Cookies.get("token");

    const response = await fetch(`${API_URL}/orders`, {
      method: "POST",
      headers: userToken && { Authorization: `Bearer ${userToken}` },
      body: JSON.stringify({
        amount: Number(Math.round(appContext.cart.total + "e2") + "e-2"),
        dishes: appContext.cart.items,
        address: data.address,
        city: data.city,
        state: data.state,
        token: token.token.id,
      }),
    });

    if (response.ok) {
      if (onOrderSuccess) {
        onOrderSuccess();
      }
    } else {
      setError(response.statusText);
    }
  }

  return (
    <div className="paper">
      <h5 className="info-header">Your information:</h5>
      <hr />
      <FormGroup style={{ display: "flex" }}>
        <div style={{ flex: "0.90", marginRight: 10 }}>
          <Label>Address</Label>
          <Input name="address" onChange={onChange} />
          {fieldErrors.address && <div style={{ color: "red" }}>{fieldErrors.address}</div>}
        </div>
      </FormGroup>
      <FormGroup style={{ display: "flex" }}>
        <div style={{ flex: "0.65", marginRight: "6%" }}>
          <Label>City</Label>
          <Input name="city" onChange={onChange} />
          {fieldErrors.city && <div style={{ color: "red" }}>{fieldErrors.city}</div>}
        </div>
        <div style={{ flex: "0.25", marginRight: 0 }}>
          <Label>State</Label>
          <Input name="state" onChange={onChange} />
          {fieldErrors.state && <div style={{ color: "red" }}>{fieldErrors.state}</div>}
        </div>
      </FormGroup>
      <CardSection data={data} stripeError={error} submitOrder={submitOrder} />
      <style jsx global>{`
        .paper {
          border: 1px solid lightgray;
          box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2),
            0px 1px 1px 0px rgba(0, 0, 0, 0.14),
            0px 2px 1px -1px rgba(0, 0, 0, 0.12);
          max-width: 900px;
          height: 550px;
          padding: 30px;
          background: #fff;
          border-radius: 6px;
          margin-top: 90px;
        }
        .info-header {
          margin-top: 0;
        }
      `}</style>
    </div>
  );
}

export default CheckoutForm;
