import { useRouter } from 'next/router';
import { ApolloProvider, ApolloClient, InMemoryCache, gql, useQuery } from '@apollo/client';

const GET_RESTAURANT_BY_ID = gql`
  query getRestaurantById($id: ID!) {
    restaurant(id: $id) {
      id
      name
      description
      image {
        url
      }
      dishes {
        id
        name
        description
      }
    }
  }
`;

const API_URL = process.env.API_URL || 'http://localhost:1337';

const RestaurantPage = () => {
  const router = useRouter();
  const { id } = router.query;

  // Initialize Apollo Client
  const client = new ApolloClient({
    uri: `${API_URL}/graphql`,
    cache: new InMemoryCache(),
  });

  const { loading, error, data } = useQuery(GET_RESTAURANT_BY_ID, {
    variables: { id },
    skip: !id // Skip the query if id is not available yet
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  const restaurant = data.restaurant;

  return (
    <ApolloProvider client={client}>
      <div>
        <h1>{restaurant.name}</h1>
        <img src={`${API_URL}${restaurant.image.url}`} alt={restaurant.name} />
        <p>{restaurant.description}</p>

        <ul>
          {restaurant.dishes.map(dish => (
            <li key={dish.id}>
              <h2>{dish.name}</h2>
              <p>{dish.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </ApolloProvider>
  );
};

export default RestaurantPage;
