import React, { useContext } from 'react';
import { useRouter } from 'next/router';
import { Button, Card, CardBody, CardTitle, Badge } from 'reactstrap';
import AppContext from './context';
import Link from 'next/link';

const CartItem = ({ item, addItem, removeItem }) => (
  <div className="item">
    <span className="item-name">{item.name}</span>
    <span className="item-price">${item.price}</span>
    <div className="item-actions">
      <Button className="item-action" onClick={() => addItem(item)}>+</Button>
      <Button className="item-action" onClick={() => removeItem(item)}>-</Button>
      <span className="item-quantity">{item.quantity}x</span>
    </div>
  </div>
);

const Cart = () => {
  const { cart, addItem, removeItem } = useContext(AppContext);
  const router = useRouter();

  const renderItems = () => (
    cart.items && cart.items.map(item => (
      item.quantity > 0 && <CartItem key={item.id} item={item} addItem={addItem} removeItem={removeItem} />
    ))
  );

  const checkoutItems = () => (
    <div className="checkout">
      <Badge className="total">
        <h5>Total:</h5>
        <h3>${cart.total}</h3>
      </Badge>
      <Link href="/checkout/">
        <Button className="checkout-button">Order</Button>
      </Link>
    </div>
  );

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      <Card className="cart">
        <CardTitle>Your Order:</CardTitle>
        <hr />
        <CardBody>
          <div className="items">
            <small>Items:</small>
            {renderItems()}
          </div>
          {checkoutItems()}
        </CardBody>
      </Card>
      <style jsx>{`
        .cart-container {
          /* Container Styles */
        }
        .cart {
          /* Cart Styles */
        }
        .item {
          display: flex;
          justify-content: space-between;
          border-bottom: 1px solid #eee;
          margin-bottom: 15px;
          padding-bottom: 10px;
        }
        .item-name {
          font-size: 1.3em;
          color: rgba(97, 97, 97, 1);
        }
        .item-price {
          font-size: 1.3em;
          color: rgba(97, 97, 97, 1);
        }
        .item-actions {
          /* Styles for the +, - buttons and the quantity text */
        }
        .item-action {
          /* Styles for each action button */
        }
        .total {
          width: 200px;
          padding: 10px;
          color: gray;
        }
        .checkout {
          /* Checkout Area Styles */
        }
        .checkout-button {
          width: 60%;
          background-color: #007bff;  /* Your blue primary color */
          color: white;
        }
      `}</style>
    </div>
  );
};

export default Cart;
