import React, { useState, useContext } from 'react';
import { FormGroup, Label, Input, FormFeedback } from 'reactstrap';
import fetch from 'isomorphic-fetch';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import CardSection from './cardSection';
import AppContext from './context';
import Cookies from 'js-cookie';

function CheckoutForm({ onOrderSuccess }) {
  const [data, setData] = useState({
    address: '',
    city: '',
    state: '',
    stripe_id: '',
  });
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});

  const stripe = useStripe();
  const elements = useElements();
  const appContext = useContext(AppContext);

  function onChange(e) {
    const updateItem = (data[e.target.name] = e.target.value);
    setData({ ...data, updateItem });
  }

  function validateFields() {
    const errors = {};
    if (!data.address) errors.address = 'Address is required';
    if (!data.city) errors.city = 'City is required';
    if (!data.state) errors.state = 'State is required';
    setFieldErrors(errors);
    return Object.keys(errors).length === 0;
  }

  async function submitOrder() {
    if (!validateFields()) {
      return;
    }
    const cardElement = elements.getElement(CardElement);
    const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:1337';
    const token = await stripe.createToken(cardElement);
    const userToken = Cookies.get('token');

    const response = await fetch(`${API_URL}/orders`, {
      method: 'POST',
      headers: userToken && { Authorization: `Bearer ${userToken}` },
      body: JSON.stringify({
        amount: Number(Math.round(appContext.cart.total + 'e2') + 'e-2'),
        dishes: appContext.cart.items,
        address: data.address,
        city: data.city,
        state: data.state,
        token: token.token.id,
      }),
    });

    if (response.ok) {
      if (onOrderSuccess) {
        onOrderSuccess();
      }
    } else {
      setError(response.statusText);
    }
  }

  return (
    <div className="paper mt-5 p-4 rounded">
      <h5>Your information:</h5>
      <hr />
      <FormGroup className="d-flex mb-3">
        <div className="flex-grow-1 mr-2">
          <Label for="address">Address</Label>
          <Input
            id="address"
            name="address"
            onChange={onChange}
            invalid={Boolean(fieldErrors.address)}
          />
          <FormFeedback>{fieldErrors.address}</FormFeedback>
        </div>
      </FormGroup>
      <FormGroup className="d-flex mb-3">
        <div className="flex-grow-1 mr-2">
          <Label for="city">City</Label>
          <Input
            id="city"
            name="city"
            onChange={onChange}
            invalid={Boolean(fieldErrors.city)}
          />
          <FormFeedback>{fieldErrors.city}</FormFeedback>
        </div>
        <div className="flex-grow-1">
          <Label for="state">State</Label>
          <Input
            id="state"
            name="state"
            onChange={onChange}
            invalid={Boolean(fieldErrors.state)}
          />
          <FormFeedback>{fieldErrors.state}</FormFeedback>
        </div>
      </FormGroup>
      <CardSection data={data} stripeError={error} submitOrder={submitOrder} />
      <style jsx global>{`
        // ... (your global styles here, if needed)
      `}</style>
    </div>
  );
}

export default CheckoutForm;
